import React from "react";
import styles from "./cardContainer.module.css";
import { toTitleCase } from "../../utils/TitleCase";

const CardContainerTitle = ({ status, length, userData, title }) => {
  return (
    <div className={styles.cardContainerTitle}>
      <div className={styles.headingLeft}>
        {status && (
          <img src={`/assets/${status}.svg`} className={styles.image} />
        )}
        {userData && (
          <>
            <div
              style={{
                width: "22px",
                height: "22px",
                backgroundColor: "#838804",
                borderRadius: "50%",
                fontSize: "12px",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                color: "white",
              }}
            > { userData.name.split(" ").map((nameFragment) => nameFragment[0].toUpperCase()) } </div>{" "}
            <p>{userData.name}</p>
          </>
        )}

        { title && <p>{title}</p> }
        <p>
          {status && toTitleCase(status)} {length}
        </p>
      </div>
      <div>
        <img src="/assets/add.svg" alt="" />
        <img src="/assets/3dot.svg" alt="" />
      </div>
    </div>
  );
};

export default CardContainerTitle;
