import styles from "./card.module.css";
import React, { useState } from "react";

const Card = ({ card, UserD }) => {
  const [isChecked, setIsChecked] = useState(false);

  // Handler to toggle the checkbox state
  const handleCheckboxClick = () => {
    setIsChecked(!isChecked);
  };
  return (
    <div className={styles.card}>
      <div className={styles.cardhead}>
        <div style={{ color: "#8c8b8b" }}>{card.id}</div>
        <p>okay</p>
      </div>
      <div className={styles.title}>
        <img src={`/assets/TODO.svg`} alt="tick" />
        <div>{card.title}</div>
      </div>
      <div className={styles.cardfooter}>
        <img src={`/assets/${card.priority}.svg`} alt="priorityimg" />
        {card.tag.map((tagValue, index) => (
          <div key={index} className={styles.tags}>
            <img src="/assets/tag.svg" alt="tag" />
            <span>{tagValue}</span>
          </div>
        ))}
      </div>
      <d></d>
    </div>
  );
};

export default Card;
