import React, { useState } from "react";
import styles from "./navbar.module.css";
import { useGroupPriority } from "../../providers/GroupPriorityProvider";

// Using the dictionary options
const GROUPING_OPTIONS = {
  STATUS: 'Status',
  USER: 'User',
  PRIORITY: 'Priority'
};

const PRIORITY_OPTIONS = {
  PRIORITY: 'Priority',
  TITLE: 'Title'
};

const Navbar = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const { grouping, priority, updateGrouping, updatePriority } =
    useGroupPriority();

  const toggleDropdown = () => {
    setDropdownOpen((prev) => !prev);
  };

  return (
    <div className={styles.navbar}>
      <div className={styles.dropdownButton} onClick={toggleDropdown}>
        <img src="/assets/Display.svg" alt="display icon" /> Display{" "}
        <img src="/assets/down.svg" alt="down icon" />
      </div>

      {isDropdownOpen && (
        <div className={styles.dropdownContent}>
          <div className={styles.dropdownField}>
            <label htmlFor="grouping">Grouping</label>
            <select
              name="grouping"
              id="grouping"
              value={grouping}
              onChange={(e) => updateGrouping(e.target.value)}
            >
              {Object.entries(GROUPING_OPTIONS).map(([key, value]) => (
                <option key={key} value={key}>
                  {value}
                </option>
              ))}
            </select>
          </div>

          <div className={styles.dropdownField}>
            <label htmlFor="priority">Priority</label>
            <select
              name="priority"
              id="priority"
              value={priority}
              onChange={(e) => updatePriority(e.target.value)}
            >
              {Object.entries(PRIORITY_OPTIONS).map(([key, value]) => (
                <option key={key} value={key}>
                  {value}
                </option>
              ))}
            </select>
          </div>
        </div>
      )}
    </div>
  );
};

export default Navbar;
