import React from "react";
import styles from "./card.module.css";

const Card = ({ card, userData }) => {
  return (
    <div className={styles.card}>
      <div className={styles.cardhead}>
        <div style={{ color: "#8c8b8b" }}>{card.id}</div>
        <div
          style={{
            width: "22px",
            height: "22px",
            backgroundColor: "#838804",
            borderRadius: "50%",
            fontSize: "12px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            color: "white",
          }}
        >
          {" "}
          {userData.name
            .split(" ")
            .map((nameFragment) => nameFragment[0].toUpperCase())}{" "}
        </div>{" "}
      </div>
      <div>{card.title}</div>
      <div className={styles.cardfooter}>
        <img src={`/assets/${card.priority}.svg`} alt="priorityimg" />
        {card.tag.map((tagValue, index) => (
          <div key={index} className={styles.tags}>
            <img src="/assets/tag.svg" alt="tag" />
            <span>{tagValue}</span>
          </div>
        ))}
      </div>
      <d></d>
    </div>
  );
};

export default Card;
